import React, { useState } from 'react';
import { generateContent } from '../api/geminiApi';

function CreateStoryPage() {
    const [storyData, setStoryData] = useState({
        name: '',
        age: '',
        interests: ''
    });
    const [story, setStory] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setStoryData({ ...storyData, [name]: value });
    };

    const handleGenerateStory = async () => {
        try {
            const result = await generateContent(storyData);
            setStory(result.content); // Adjust based on the API response structure
        } catch (error) {
            console.error('Error generating story:', error);
        }
    };

    return (
        <div>
            <h2>Создание истории</h2>
            <form>
                <div>
                    <label>
                        Имя:
                        <input type="text" name="name" value={storyData.name} onChange={handleInputChange} />
                    </label>
                </div>
                <div>
                    <label>
                        Возраст:
                        <input type="number" name="age" value={storyData.age} onChange={handleInputChange} />
                    </label>
                </div>
                <div>
                    <label>
                        Интересы:
                        <input type="text" name="interests" value={storyData.interests} onChange={handleInputChange} />
                    </label>
                </div>
                <button type="button" onClick={handleGenerateStory}>Сгенерировать историю</button>
            </form>
            {story && (
                <div>
                    <h3>Сгенерированная история:</h3>
                    <p>{story}</p>
                </div>
            )}
        </div>
    );
}

export default CreateStoryPage;
