import { GEMINI_API_URL } from '../config';

export const generateContent = async (data) => {
    try {
        // Изменяем prompt, убирая инструкцию о кнопке воспроизведения
        const prompt = `Напиши детскую сказку о космических приключениях для ребенка по имени ${data.name}, которому ${data.age} лет и который любит ${data.interests}. История должна быть добрая и увлекательная, подходящая для его возраста.`;

        // Подготавливаем данные для запроса в правильном формате
        const requestData = {
            contents: [{
                parts: [{ text: prompt }]
            }]
        };

        const response = await fetch(GEMINI_API_URL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestData)
        });

        if (!response.ok) {
            throw new Error('Network response was not ok');
        }

        const result = await response.json();
        
        // Извлекаем текст истории из ответа
        const story = result.candidates[0].content.parts[0].text;
        return { content: story };
    } catch (error) {
        console.error('Error fetching data from Gemini API:', error);
        throw error;
    }
};
